    override func viewDidLoad() {
        super.viewDidLoad()
        
        searchBtn.setTitle("", for: .normal)
        // Do any additional setup after loading the view.
    }
    
    @IBAction func search(_ sender: Any) {
        if (searchField.text != ""){
            
        let result = "Result for : " + searchField.text!
        
        resultTextLabel.text = result + "\n \n" + textSearched
        resultTextLabel.textAlignment = .left
        
        searchField.text = ""
        }
        else{
            resultTextLabel.text = "Search box should not be empty!!"
            searchField.text = ""
        }

        searchField.endEditing(true)
        resultTextLabel.isEditable = false
    }

    override func touchesBegin(_ touches: Set<UITouch>, with event: UIEvent?){
        searchField.endEditing(true)
        resultTextLabel.endEditing(true)
    }
    
